    def showTime(self):
        time = QTime.currentTime()
        text = time.toString('hh:mm:ss')
        if (time.second() % 2) == 0:
            text =text[:2]+":"+text[3:]# + ' ' + text[:4]'''
        self.lcdHora.display(text)
    
